import React, { FC } from "react";
import { Forum } from "../models/forum";
import { Grid, GridItem } from "@chakra-ui/react";
import ForumListItem from "./ForumList-Item";

interface ForumListProps {
    forums: Forum[];
    onDeleteClick: (forumId: string) => void;
}

export const ForumList: FC<ForumListProps> = ({ forums, onDeleteClick }) => {
    return (
        <Grid
            sx={{
                gridTemplateColumns: "repeat(1, 1fr)",
                gap: 10,
                padding: 5,
                borderWidth: 2,
                borderRadius: "md",
                borderColor: "gray.300",
                backgroundColor: "gray.50",
            }}
        >
            {forums.map((forumItem: Forum) => (
                <GridItem key={forumItem.id}>
                    <ForumListItem forum={forumItem} onDeleteClick={onDeleteClick} />
                </GridItem>
            ))}
        </Grid>
    );
};
