import React from "react";
import {
    Modal,
    ModalOverlay,
    ModalContent,
    ModalHeader,
    ModalFooter,
    ModalBody,
    ModalCloseButton,
    Button,
    Text
} from "@chakra-ui/react";

interface DeleteConfirmationProps {
    isOpen: boolean;
    onClose: () => void;
    onConfirm: () => void;
}

const DeleteConfirmation: React.FC<DeleteConfirmationProps> = ({ isOpen, onClose, onConfirm }) => {
    return (
        <Modal isOpen={isOpen} onClose={onClose}>
            <ModalOverlay />
            <ModalContent>
                <ModalHeader>Fórum törlése</ModalHeader>
                <ModalCloseButton />
                <ModalBody>
                    <Text>Biztosan törölni szeretné ezt a fórumot? Ez a művelet végleges és nem visszavonható.</Text>
                </ModalBody>
                <ModalFooter>
                    <Button colorScheme="red" onClick={onConfirm} marginRight={3}>
                        Törlés
                    </Button>
                    <Button variant="ghost" onClick={onClose}>
                        Mégse
                    </Button>
                </ModalFooter>
            </ModalContent>
        </Modal>
    );
};

export default DeleteConfirmation;
